    alias: 'controller.ACLController',

    onComponentRender: function(component, eOpts) {
        if (!Ext.isEmpty(component.ACL)) {
            if (ACL.interfaceRights[component.ACL]===false){
                component.hide();
                component.clearListeners();
                component.fireEvent=Ext.emptyFn;
            }
        }
    },

