Ext.define('Rubedo.controller.FOThemesController', {
    extend: 'Ext.app.Controller',
    alias: 'controller.FOThemesController',

    onFoThemeImportBtnClick: function(button, e, eOpts) {
        Ext.widget("FOThemeImportWindow").show();
    },

    onFOThemeZipSubmitBtnClick: function(button, e, eOpts) {

        var form=button.up().getForm();
        if (form.isValid()){
             button.up().setLoading(true);
            form.submit({
                url: 'fo-themes/import',
                params: {

                },
                success: function(form, action) {
                    button.up().setLoading(false);
                    button.up().up().up().close();
                },
                failure: function(form, action) {
                    button.up().setLoading(false);
                    switch (action.failureType) {
                        case Ext.form.action.Action.CLIENT_INVALID:
                        Ext.Msg.alert('Erreur', 'Certains champs sont invalides');
                        break;
                        case Ext.form.action.Action.CONNECT_FAILURE:
                        Ext.Msg.alert('Erreur', 'Erreur Ajax');
                        break;
                        case Ext.form.action.Action.SERVER_INVALID:
                        Ext.Msg.alert('Erreur', action.result.msg);
                    }
                }
            });
        }
    },

    init: function(application) {
        this.control({
            "#foThemeImportBtn": {
                click: this.onFoThemeImportBtnClick
            },
            "#FOThemeZipSubmitBtn": {
                click: this.onFOThemeZipSubmitBtnClick
            }
        });
    }

});
