Ext.define('Rubedo.controller.FOThemesController', {
    extend: 'Ext.app.Controller',
    alias: 'controller.FOThemesController',

    onFoThemeImportBtnClick: function(button, e, eOpts) {
        Ext.widget("FOThemeImportWindow").show();
    },

    onFOThemeZipSubmitBtnClick: function(button, e, eOpts) {

        var form=button.up().getForm();
        if (form.isValid()){
            button.up().setLoading(true);
            form.submit({
                url: 'fo-themes/import',
                params: {

                },
                success: function(form, action) {
                    Ext.getStore("ThemeDirectoriesStore").load();
                    button.up().setLoading(false);
                    button.up().up().close();
                    Ext.Msg.alert('Success', "Archive imported");
                },
                failure: function(form, action) {
                    Ext.getStore("ThemeDirectoriesStore").load();
                    button.up().setLoading(false);
                    switch (action.failureType) {
                        case Ext.form.action.Action.CLIENT_INVALID:
                            Ext.Msg.alert('Erreur', 'Certains champs sont invalides');
                            break;
                        case Ext.form.action.Action.CONNECT_FAILURE:
                            Ext.Msg.alert('Erreur', 'Erreur Ajax');
                            break;
                        case Ext.form.action.Action.SERVER_INVALID:
                            Ext.Msg.alert('Erreur', action.result.msg);
                    }
                }
            });
        }
    },

    onFOThemesInterfaceAfterRender: function(component, eOpts) {
        Ext.getStore("ThemeDirectoriesStore").load();
    },

    onThemeFileManagerGridSelectionChange: function(model, selected, eOpts) {
        if (Ext.isEmpty(selected)){
            Ext.getCmp("themeFileBtnGroup").disable();
        } else {
            Ext.getCmp("themeFileBtnGroup").enable();
            if (selected.length==1){
                Ext.getCmp("themeFileBtnGroup").getComponent(1).enable();
                Ext.getCmp("themeFileBtnGroup").getComponent(0).enable();
            } else {
                Ext.getCmp("themeFileBtnGroup").getComponent(1).disable();
                Ext.getCmp("themeFileBtnGroup").getComponent(0).disable();
            }
        }
    },

    init: function(application) {
        this.control({
            "#foThemeImportBtn": {
                click: this.onFoThemeImportBtnClick
            },
            "#FOThemeZipSubmitBtn": {
                click: this.onFOThemeZipSubmitBtnClick
            },
            "#FOThemesInterface": {
                afterrender: this.onFOThemesInterfaceAfterRender
            },
            "#themeFileManagerGrid": {
                selectionchange: this.onThemeFileManagerGridSelectionChange
            }
        });
    }

});
