Ext.define('Rubedo.view.FacetOverriderField', {
    extend: 'Ext.form.field.Hidden',
    alias: 'widget.FacetOverriderField',

    queryMode: true,
    geoQueryMode: false,
    damQueryMode: false,
    fieldLabel: 'Label',
    labelSeparator: ' ',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            listeners: {
                render: {
                    fn: me.onHiddenfieldRender,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    onHiddenfieldRender: function(component, eOpts) {
        var myComponent = Ext.widget("FacetOverriderFieldComponent");
        myComponent.setFieldLabel(component.fieldLabel+" ");
        component.on("change", function(a,newValue){
            if (Ext.isEmpty(newValue)){
                myComponent.getComponent("addBtn").show();
                myComponent.getComponent("editBtn").hide();
                myComponent.getComponent("removeBtn").hide();
            } else {
                myComponent.getComponent("addBtn").hide();
                myComponent.getComponent("editBtn").show();
                myComponent.getComponent("removeBtn").show();
            }
        });
        myComponent.getComponent("removeBtn").on("click", function(){
            component.setValue(null);
        });
        myComponent.getComponent("addBtn").on("click", function(){
            var usedFacets=component.findParentByType("window").query("FTDComboField")[0].getValue();
            var initialValue= [ ];
            if ((!Ext.isEmpty(usedFacets))&&(!Ext.Array.contains(usedFacets,"all"))){
                var store=component.findParentByType("window").query("FTDComboField")[0].getStore();
                Ext.Array.forEach(usedFacets, function(facet){
                    initialValue.push({id:facet, facetOperator:store.findRecord("id",facet).get("facetOperator")});
                });
            }
            var editor=Ext.widget("FacetOverriderWindow");
            editor.initialValue=initialValue;
            editor.targetedFieldId=component.getId();
            editor.show();

        });

        myComponent.getComponent("editBtn").on("click", function(){
            var editor=Ext.widget("FacetOverriderWindow");
            editor.initialValue=Ext.JSON.decode(component.getValue());
            editor.targetedFieldId=component.getId();
            editor.show();
        });
        component.up().add(myComponent);
        component.fireEvent("change",component, component.getValue());

    }

});