    onTextfieldChange: function(field, newValue, oldValue, eOpts) {
        if (Ext.isEmpty(newValue)){
            field.addListener("blur", function(){
                if (Ext.isEmpty(field.getValue())){field.setValue(";");}
            }, this, {single:true});
            }
    },

