Ext.define('Rubedo.view.MultiChoiceConfigurator', {
    extend: 'Ext.window.Window',
    alias: 'widget.MultiChoiceConfigurator',

    height: 360,
    id: 'MultiChoiceConfigurator',
    width: 632,
    resizable: false,
    layout: {
        type: 'fit'
    },
    title: 'Editeur de question choix multiple',
    constrain: true,
    modal: true,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            dockedItems: [
                {
                    xtype: 'toolbar',
                    dock: 'bottom',
                    items: [
                        {
                            xtype: 'tbfill'
                        },
                        {
                            xtype: 'button',
                            id: 'MultiChoiceConfiguratorSubmit',
                            iconCls: 'ouiSpetit',
                            text: 'Valider',
                            listeners: {
                                click: {
                                    fn: me.onMultiChoiceConfiguratorSubmitClick,
                                    scope: me
                                }
                            }
                        }
                    ]
                }
            ],
            items: [
                {
                    xtype: 'form',
                    bodyPadding: 10,
                    title: '',
                    items: [
                        {
                            xtype: 'textfield',
                            anchor: '100%',
                            name: 'fieldLabel',
                            fieldLabel: 'Label de la question',
                            labelWidth: 140
                        },
                        {
                            xtype: 'textfield',
                            anchor: '100%',
                            name: 'qNb',
                            fieldLabel: 'Code de la question',
                            labelWidth: 140
                        },
                        {
                            xtype: 'textfield',
                            anchor: '100%',
                            name: 'tooltip',
                            fieldLabel: 'Bulle d\'aide',
                            labelWidth: 140
                        },
                        {
                            xtype: 'checkboxfield',
                            anchor: '100%',
                            name: 'allowBlank',
                            fieldLabel: 'Faculatif',
                            labelWidth: 140,
                            boxLabel: '',
                            inputValue: 'true'
                        },
                        {
                            xtype: 'numberfield',
                            anchor: '100%',
                            name: 'columns',
                            fieldLabel: 'Nombre de colonnes',
                            labelWidth: 140,
                            allowDecimals: false,
                            minValue: 1
                        },
                        {
                            xtype: 'radiogroup',
                            fieldLabel: 'Mode',
                            labelWidth: 140,
                            items: [
                                {
                                    xtype: 'radiofield',
                                    name: 'fieldType',
                                    boxLabel: 'Une seule réponse',
                                    inputValue: 'radiogroup'
                                },
                                {
                                    xtype: 'radiofield',
                                    name: 'fieldType',
                                    boxLabel: 'Plusieurs réponses',
                                    inputValue: 'checkboxgroup'
                                }
                            ]
                        }
                    ]
                }
            ],
            listeners: {
                render: {
                    fn: me.onWindowRender,
                    scope: me
                },
                afterrender: {
                    fn: me.onWindowAfterRender,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    onMultiChoiceConfiguratorSubmitClick: function(button, e, options) {
        var initialValues = Ext.clone(button.up().up().initialItemConfig);
        var myId = Ext.clone(button.up().up().targetedId);
        var form = button.up().up().getComponent(0).getForm();
        if (form.isValid()) {
            var newData = Ext.clone(form.getFieldValues());
            initialValues.fieldType=newData.fieldType;
            initialValues.label=newData.fieldLabel;
            initialValues.tooltip=newData.tooltip;
            initialValues.qNb=newData.qNb;
            delete newData.fieldType;
            delete newData.fieldLabel;
            delete newData.tooltip;
            delete newData.qNb;
            newData.vertical=true;
            newData.items=[];
            Ext.Array.forEach(newData.answers, function(answer,index){
                newData.items.push({
                    boxLabel:answer,
                    inputValue:index,
                    name:myId
                });
            });
            delete newData.answers;
            initialValues.fieldConfig=newData;
            Ext.getCmp(myId).itemConfig=initialValues;
            button.up().up().close();
            Ext.getCmp(myId).sync();
        }
    },

    onWindowRender: function(abstractcomponent, options) {
        var optionPicker = Ext.create("Ext.ux.form.field.BoxSelect", {
            store:[],
            anchor:"100%",
            name:"answers",
            fieldLabel:"Réponses possibles",
            labelWidth:140,
            queryMode:"local",
            submitValue:true,
            multiSelect:true,
            forceSelection:false,
            createNewOnEnter:true,
            hideTrigger:true,
            triggerOnClick:false,
            createNewOnBlur:true,
            stacked:true,
            allowBlank:true
        });
        abstractcomponent.getComponent(0).add(optionPicker);
    },

    onWindowAfterRender: function(abstractcomponent, options) {
        var initialValues = Ext.clone(abstractcomponent.initialItemConfig.fieldConfig);
        initialValues.fieldType= Ext.clone(abstractcomponent.initialItemConfig.fieldType);
        initialValues.answers=Ext.Array.pluck(initialValues.items, "boxLabel");
        initialValues.fieldLabel=Ext.clone(abstractcomponent.initialItemConfig.label);
        initialValues.tooltip=Ext.clone(abstractcomponent.initialItemConfig.tooltip);
        initialValues.qNb=Ext.clone(abstractcomponent.initialItemConfig.qNb);
        delete initialValues.items;
        delete initialValues.vertical;
        abstractcomponent.getComponent(0).getForm().setValues(initialValues);
    }

});