Ext.define('Rubedo.view.MyGridPanel28', {
    extend: 'Ext.grid.Panel',
    alias: 'widget.mygridpanel28',

    requires: [
        'Rubedo.view.override.MyGridPanel28',
        'Ext.ux.container.SwitchButtonSegment'
    ],

    managesStore: true,
    id: 'imagesSpecialGrid',
    title: '',
    forceFit: true,
    store: 'ImagePickerStore',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            viewConfig: {

            },
            columns: [
                {
                    xtype: 'gridcolumn',
                    dataIndex: 'filename',
                    text: 'Fichier'
                },
                {
                    xtype: 'gridcolumn',
                    renderer: function(value, metaData, record, rowIndex, colIndex, store, view) {
                        return(value.fullName);
                    },
                    dataIndex: 'createUser',
                    text: 'Auteur'
                },
                {
                    xtype: 'datecolumn',
                    dataIndex: 'createTime',
                    text: 'Date de cr√©ation',
                    format: 'd-m-Y'
                }
            ],
            listeners: {
                itemdblclick: {
                    fn: me.onImagesSpecialGridItemDblClick,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    onImagesSpecialGridItemDblClick: function(tablepanel, record, item, index, e, options) {
        var fenetre = Ext.getCmp(record.get("filename")+"NadPreview");
        if (Ext.isDefined(fenetre)){ fenetre.toFront(); }
        else {
            fenetre = Ext.widget('ImagePreviewWindow', {title:record.get("filename"), id:record.get("filename")+"NadPreview"});
            fenetre.getComponent(0).setSrc("file/get/file-id/"+record.get("id"));
            Ext.getCmp('desktopCont').add(fenetre);
            fenetre.show();
        }
    }

});