Ext.define('Rubedo.view.MyGridPanel31', {
    extend: 'Ext.grid.Panel',
    alias: 'widget.mygridpanel31',

    requires: [
        'Ext.grid.View',
        'Ext.grid.plugin.DragDrop',
        'Ext.grid.column.Boolean',
        'Ext.toolbar.Paging'
    ],

    id: 'manualQueryLeftGrid',
    title: '',
    store: 'ContentSelectorStore2',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            viewConfig: {
                plugins: [
                    Ext.create('Ext.grid.plugin.DragDrop', {

                    })
                ]
            },
            columns: [
                {
                    xtype: 'gridcolumn',
                    renderer: function(value, metaData, record, rowIndex, colIndex, store, view) {
                        if (record.get("status")=="published") {
                            return('<img src="resources/icones/'+MyPrefData.iconsDir+'/16x16/page_accept.png"> '+value);
                        } else if (record.get("status")=="pending") {
                            return('<img src="resources/icones/'+MyPrefData.iconsDir+'/16x16/page_process.png"> '+value);
                        } else if (record.get("status")=="draft") {
                            return('<img src="resources/icones/'+MyPrefData.iconsDir+'/16x16/page_edit.png"> '+value);
                        }



                    },
                    localiserId: 'u1TitleColumn',
                    dataIndex: 'text',
                    text: 'Titre',
                    flex: 1
                },
                {
                    xtype: 'gridcolumn',
                    renderer: function(value, metaData, record, rowIndex, colIndex, store, view) {
                        try{var myFlagCode=Ext.getStore("AllLanguagesStore3").query("locale",record.get("locale"),false,false,true).items[0].get("flagCode");}
                        catch(err){var myFlagCode="_unknown";}
                        var returner =" <img src=\"/assets/flags/16/"+myFlagCode+".png\"> ";
                        if(!Ext.isEmpty(value)){
                            Ext.Object.each(value, function(key, value, myself) {
                                if (key!=record.get("locale")){
                                    try{var myFlagCode2=Ext.getStore("AllLanguagesStore3").query("locale",key,false,false,true).items[0].get("flagCode");}
                                    catch(err){var myFlagCode2="_unknown";}
                                    if (myFlagCode2!="_unknown"){
                                        returner=returner+" <img src=\"/assets/flags/16/"+myFlagCode2+".png\"> ";
                                    }
                                }
                            });
                        }
                        return(returner);
                    },
                    dataIndex: 'i18n',
                    text: 'Languages',
                    flex: 0.7,
                    listeners: {
                        afterrender: {
                            fn: me.onGridcolumnAfterRender,
                            scope: me
                        }
                    }
                },
                me.processType({
                    xtype: 'gridcolumn',
                    renderer: function(value, metaData, record, rowIndex, colIndex, store, view) {
                        if (Ext.isEmpty(Ext.getStore("TCNDepComboCS").findRecord("id",value))) {
                            return(value);
                        } else {
                            return(Ext.getStore("TCNDepComboCS").findRecord("id",value).get("type"));
                        }
                    },
                    localiserId: 'u1TypeColumn',
                    dataIndex: 'typeId',
                    text: 'Type',
                    flex: 1
                }),
                me.processEtat({
                    xtype: 'gridcolumn',
                    renderer: function(value, metaData, record, rowIndex, colIndex, store, view) {
                        if (value=="published") {
                            return("publi√©");
                        } else if (value=="pending") {
                            return("en attente de validation");
                        } else if (value=="draft") {
                            return("brouillon");
                        }
                    },
                    localiserId: 'u1StatusColumn',
                    dataIndex: 'status',
                    text: 'Etat',
                    flex: 1
                }),
                me.processEnligne({
                    xtype: 'booleancolumn',
                    filter: {
                        type: 'combo',
                        store: [
                            [
                                true,
                                'Oui'
                            ],
                            [
                                false,
                                'Non'
                            ]
                        ]
                    },
                    localiserId: 'u1OnlineColumn',
                    width: 60,
                    dataIndex: 'online',
                    text: 'En ligne',
                    falseText: 'Non',
                    trueText: 'Oui'
                })
            ],
            dockedItems: [
                {
                    xtype: 'pagingtoolbar',
                    dock: 'bottom',
                    id: 'contentSelectorPagingToolbar',
                    width: 360,
                    displayInfo: true,
                    listeners: {
                        beforerender: {
                            fn: me.onPagingToolbarConentSelectBeforeRender,
                            scope: me
                        }
                    }
                }
            ]
        });

        me.processMyGridPanel31(me);
        me.callParent(arguments);
    },

    processType: function(config) {
        config.filter={
            type:"list",
            labelField:"type",
            store:Ext.getStore("TCNDepComboCS")
        };
        return config;
    },

    processEtat: function(config) {
        config.filter={
            type:"list",
            options: [
            ["draft", Rubedo.RubedoAutomatedElementsLoc.draftText],
            ["pending", Rubedo.RubedoAutomatedElementsLoc.pendingText],
            ["published", Rubedo.RubedoAutomatedElementsLoc.publishedText],
            ["refused", Rubedo.RubedoAutomatedElementsLoc.refusedText]
            ]
        };
        return config;
    },

    processEnligne: function(config) {
        config.trueText=Rubedo.RubedoAutomatedElementsLoc.yesText;
        config.falseText=Rubedo.RubedoAutomatedElementsLoc.noText;
        config.filter={
            type:"list",
            options: [
            [true, config.trueText],
            [false,config.falseText]
            ]
        };
        return config;
    },

    processMyGridPanel31: function(config) {
        config.features=[Ext.create("Ext.ux.grid.FiltersFeature",{encode:true,local:false})];
        return config;
    },

    onGridcolumnAfterRender: function(component, eOpts) {
        if (Ext.getStore("AllLanguagesStore3").getRange().length==1){
            component.hide();
        }
    },

    onPagingToolbarConentSelectBeforeRender: function(component, eOpts) {
        component.bindStore(component.up().getStore());
    }

});