Ext.define('Rubedo.view.sendEmailWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.sendEmailWindow',

    requires: [
        'Rubedo.view.MailingListPickerField'
    ],

    id: 'sendEmailWindow',
    width: 400,
    layout: {
        type: 'fit'
    },
    title: 'Send email',
    modal: true,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'form',
                    bodyPadding: 10,
                    title: '',
                    items: [
                        {
                            xtype: 'MailingListPickerField',
                            anchor: '100%',
                            fieldLabel: 'Mailing list',
                            name: 'mailingList',
                            allowBlank: false,
                            allowOnlyWhitespace: false
                        },
                        {
                            xtype: 'button',
                            anchor: '100%',
                            id: 'sendEmailSubmitBtn',
                            text: 'Send',
                            listeners: {
                                click: {
                                    fn: me.onSendEmailSubmitBtnClick,
                                    scope: me
                                }
                            }
                        }
                    ]
                }
            ]
        });

        me.callParent(arguments);
    },

    onSendEmailSubmitBtnClick: function(button, e, eOpts) {
        var form=button.up().getForm();
        if (form.isValid()){
            Ext.Ajax.request({
                url: 'emails/send',
                method: 'POST',
                params: {
                    list: form.getValues().mailingList,
                    id:Ext.getCmp("mainETGrid").getSelectionModel().getLastSelected().get("id")
                },
                success: function(response){
                    var text = Ext.JSON.decode(response.responseText);
                    if (text.success){
                        Ext.Msg.alert("Success", "Mail sent");
                        button.up().up().close();
                    } else {
                        Ext.Msg.alert("Error", "Error sending mail");
                    }
                }
            });
        }
    }

});