Ext.define('Rubedo.view.sessionWarningWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.sessionWarningWindow',

    height: 82,
    id: 'sessionWarningWindow',
    width: 252,
    resizable: false,
    layout: {
        type: 'fit'
    },
    title: 'Votre session va bientot expirer',
    modal: true,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'button',
                    handler: function(button, event) {
                        Ext.Ajax.request({
                            url:'acl',
                            params:{
                                data: Ext.JSON.encode(ACL.interfaceRights)
                            },
                            success:function(response){
                                ACL.interfaceRights=Ext.JSON.decode(response.responseText);
                            },
                            failure:function(){
                                Ext.Msg.alert('Erreur', 'Erreur dans la récupération des droits');
                            }
                        });
                        button.up().close();
                    },
                    text: 'Renouveler ma session'
                }
            ]
        });

        me.callParent(arguments);
    }

});